    def connect_to_server(self):
        if not self.chat_client_obj:
            addr = self.serverIPLineEdit.text()
            port = self.serverPortLineEdit.text()
            login = self.loginLineEdit.text()
            self.chat_client_obj = ChatClient(addr, port, login)
            self.sock = self.chat_client_obj.run_socket()

            write_message_to_sock(self.chat_client_obj.create_presence(), self.sock)
            presence_answer = self.chat_client_obj.process_ans(read_message_from_sock(self.sock))
            if presence_answer == '200 : OK':
                self.chatTextBrowser.append('Соединение с сервером успешно установлено.')
            else:
                self.chatTextBrowser.append(f'Сервер вернул ошибку: {presence_answer}')

            self.get_contacts()

            receiving_thread = threading.Thread(target=self.receive_msg)
            receiving_thread.daemon = True
            receiving_thread.start()

            sending_msg_thread = threading.Thread(target=self.send_msg)
            sending_msg_thread.daemon = True
            sending_msg_thread.start()

    def send_msg(self):
        if (self.contactsListWidget.currentItem() is not None) & (self.msgTextLineEdit.text() != ''):
            message_receiver = self.contactsListWidget.currentItem().text()
            text = self.msgTextLineEdit.text()
            message = self.chat_client_obj.create_text_message(message_receiver, text)
            try:
                write_message_to_sock(message, self.sock)
                self.chat_client_obj.store_msg_to_db(message_receiver, text)
            except socket.error as e:
                self.chatTextBrowser.append(f'потеряно соединение с сервером. {e}')

    def receive_msg(self):
        while True:
            try:
                message = read_message_from_sock(self.sock)
                if message:
                    self.chatTextBrowser.append(f'{message[SENDER]}: {message[MESSAGE_TEXT]}')
            except socket.error as e:
                self.chatTextBrowser.append(f'потеряно соединение с сервером. {e}')

    def get_contacts(self):
        self.contactsListWidget.addItems(self.chat_client_obj.get_contacts_from_db())

